<script setup>
import OButton from 'src/components/Button/OButton.vue'
import OInput from 'src/components/Input/OInput.vue'
import { useAuthStore } from 'src/stores/auth.store'
import { onMounted, ref } from 'vue'
import { useRouter } from 'vue-router'
import OCheck from 'src/components/Checkbox/OCheck.vue'
import gsap from 'gsap/dist/gsap'
import imageLogin from 'src/assets/image/bg-login.jpg'
import { storeToRefs } from 'pinia'
import { useDarkMode } from 'src/stores/darkMode'
import { useUserStore } from 'src/stores/usuarios/user.store'

const { getUser } = useUserStore()

const dark = useDarkMode()
// const { darkMode } = storeToRefs(dark)
const form = ref(null)
const type = ref('password')
const login = ref(null)
const router = useRouter()
const load = ref(false)
const permanecerConectado = ref(true)
const dev = process.env.development

const data = ref({
  login: dev ? 'emanuel2' : '',
  senha: dev ? 'papa1539' : '',
})

onMounted(() => {
  form.value.focus()

  gsap.set('.form-login', {
    opacity: 0,
    y: 90,
  })

  gsap.to('.form-login', {
    opacity: 1,
    y: 0,
    ease: 'power1.out',
  })

  gsap.to('.letras', {
    rotate: 360,
    repeat: -1,
    ease: 'none',
    duration: 20,
  })
})

async function onSubmit() {
  load.value = true
  const authStore = useAuthStore()

  const logged = await authStore.login(data.value.login, data.value.senha)
  if (logged) {
    router.push({ path: '/' })
    getUser()
  }

  // else form.value.isInvalid()
  load.value = false
}
</script>

